    async Task OnSearch(ChangeEventArgs e)
    {
        var text = e.Value?.ToString();
        if (text is not null && text.Length > 3)
        {
            movies = await TmdbApi.Search(text);
            await InvokeAsync(StateHasChanged);
        }
    }
